<?php
class Picture extends Database {

    public function upload($username, $header, $description, $file){
        //$file_ext=strtolower(end(explode('.',$file['image']['name'])));
        //$base64 = $base64 = 'data:image/' . $file_ext . ';base64,' . base64_encode(file_get_contents($file['image']['tmp_name']));
        //$base64 = "data:image/png;base64,";


        $sql = "INSERT INTO picture (user, header, description, picture) VALUES ( :username , :header , :description , :file );";

       // INSERT INTO picture (user, header, description, picture) VALUES ( 'JohnDoe' , 'sdfsfs' , 'sdfsgf' , 'data:image/png;base64,');

        $stmt = $this->conn->prepare($sql);
        $stmt->bindParam(':username', $username);
        $stmt->bindParam(':header', $header);
        $stmt->bindParam(':description', $description);
        $stmt->bindParam(':file', $file);
        $stmt->execute();


        return true;
    }


    public function getAll () {

        $sql = "SELECT * FROM picture;";

        $stmt = $this->conn->prepare($sql);
        $stmt->execute();

        $result = $stmt->fetchAll();
        $jsonreturn = json_encode($result);
        return $result;
    }

}